import { NavBar } from "./NavBar";
import { useEffect,useState } from "react";
import { createSearchParams, useSearchParams } from "react-router-dom";

const MovieDetails = ()=>{
    const [details, setDetails] = useState([]);
    const  [searchParam] =useSearchParams();
   const id = searchParam.get('id');

    const getDetails = ()=>{
    var requestOptions = {
        method: 'GET',
        redirect: 'follow'
      };

      fetch(`https://api.themoviedb.org/3/movie/${id}}/credits?api_key=a971131533ecd1f4d0cb562ab92a94ef`, requestOptions)
        .then(response => response.json())
        .then(details => setDetails(details.results))
        .catch(error => console.log('error', error));

    }
    useEffect(() => {
        getDetails()
    }, [])
    console.log(details);

    return (
        <>
        <NavBar/>

        <div className="mainMovieDetailsContainer">
        <div className="mainDiv" onClick={()=> moveTo()}>
                {details.map(item =>
                    <div className="postContainer" key={item.id}>
                        <img className="posterImage" src={`http://image.tmdb.org/t/p/w500/${item.poster_path}`} alt={item.title} />
                        {/* <div className="circularProgressBar">
                        <CircularProgressbar
                            value={item.vote_average}
                            text={item.vote_average}
                            styles={buildStyles({
                                strokeLinecap: 'butt',
                                textSize: '30px',
                                // pathTransitionDuration: 0.5,
                                textColor: '#f88',
                                backgroundColor: '#101010',
                                trailColor: '#339933',

                            })}
                            />
                        </div> */}
                        <div className="movieDescreption">
                            <h4 className="movieTitle">{item.title}</h4>
                            <p className="pubDate">{item.release_date}</p>

                        </div>
                    </div>

                )}
            </div>


        </div></>

    )
}

export default MovieDetails;
