import { NavBar } from "./NavBar";
import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { selectedMovie } from "../redux/actions/moviesActions";


    const MovieDetails = () => {
     const {movieId} = useParams();
     const movie = useSelector((state) => state.movie);
     const dispatch = useDispatch();


     const getDetails = () => {

        fetch(`https://api.themoviedb.org/3/movie/${movieId}?api_key=a971131533ecd1f4d0cb562ab92a94ef&language=en-US
        `)
            .then(response => response.json())
            .then(data => {dispatch(selectedMovie(data))},)
            .catch(error => console.log('error', error));

     }

    useEffect(() => {
        getDetails()
    },[dispatch]);


    const movieCast = () => {
        fetch(`https://api.themoviedb.org/3/movie/${movieId}/credits?api_key=a971131533ecd1f4d0cb562ab92a94ef&language=en-US`)
            .then(response => response.json())
            .then(data => {dispatch(movieCast(data))},)
            .catch(error => console.log('error', error));

     }

    }


export default MovieDetails;
