import { ActionTypes } from "../"

const initialState = {
    movies:[],
}

export const movieReducer = (state=initialState, {type,payload})=> {
    switch(type){
        case ActionTypes.SET_MOVIES:
            return {...state, movie:payload};

        default:
            return state;   
    }
}
