import { useEffect, useState } from "react";

export function GetMovies() {
    const [data, setData] = useState([]);

    // const apiGet =  () => {
    useEffect(() => {
        function getMovies() {
            var requestOptions = {
                method: 'GET',
                redirect: 'follow'
            };

            fetch("https://api.themoviedb.org/3/movie/popular?api_key=a971131533ecd1f4d0cb562ab92a94ef", requestOptions)
                .then(response => response.json())
                .then(data => setData(data.results))
                .catch(error => console.log('error', error));
        }
        getMovies();
    })
console.log(data)
    return (
        <>
            {data.map(d=>
                <div className="mainDiv">
                    <div className="postContainer">
                        {/* <img className="posterImage" src="https://media.harrypotterfanzone.com/philosophers-stone-20-years-of-movie-magic-ron-poster.jpg" alt="movie poster"></img> */}
                        <img className="posterImage" src={`d.backdrop_path`} alt='f' ></img>
                        <div className="movieDescreption">
                            <h4 className="movieTitle">{d.title}</h4>
                            <p className="pubDate">Feb.2.2012</p>
                        </div>
                    </div>

                </div>
            )}
        </>

    );
}
