import { useEffect } from "react";
import { useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { movieCast } from "../redux/actions/moviesActions";



const MovieCast = () => {
    const { movieId } = useParams();
    const movie = useSelector((state) => state.MovieCast.movie);
    const dispatch = useDispatch()


    const movieCast = () => {

        fetch(`https://api.themoviedb.org/3/movie/${movieId}/credits?api_key=a971131533ecd1f4d0cb562ab92a94ef&language=en-US`)
            .then(response => response.json())
            .then(data => { dispatch(movieCast(data)) },)
            .catch(error => console.log('error', error));

    }

    useEffect(() => {
        movieCast()
    }, [dispatch]);
}



export default MovieCast;
