export function MoviesHeader() {
    const [data,setData]= useState([]);
    const [active, setActive] = useState(false);


    const getMoviesToday = () =>{
        var requestOptions = {
            method: 'GET',
            redirect: 'follow'
          };

          fetch("https://api.themoviedb.org/3/trending/movie/day?api_key=a971131533ecd1f4d0cb562ab92a94ef", requestOptions)
            .then(response => response.text())
            .then(result => console.log(result))
            .catch(error => console.log('error', error));

            return(
                ` <>
                <div className="mainDiv" onClick={()=> moveTo()}>
                        {data.map(item =>
                            <div className="postContainer" key=${item.id}>
                                <img className="posterImage" src={"http://image.tmdb.org/t/p/w500/${item.poster_path}"} alt={item.title} />
