                                <CircularProgressbar
                                    value=${item.vote_average}
                                    text=${item.vote_average}
                                    styles={buildStyles({
                                        strokeLinecap: 'butt',
                                        textSize: '30px',
                                        // pathTransitionDuration: 0.5,
                                        textColor: '#f88',
                                        backgroundColor: '#101010',
                                        trailColor: '#339933',

                                    })}
                                    />
                                </div> */}
                                <div className="movieDescreption">
                                    <h4 className="movieTitle">${item.title}</h4>
                                    <p className="pubDate">${item.release_date}</p>

                                </div>
                            </div>

                        )}
                    </div>
                </>`
            )


    }

    useEffect(() => {
        getMoviesToday();

    }, [])

    const handleClick = () => {
        setActive(!active);
    };

    let baseStyle={
        color:active ? 'black' :  'rgb(126, 190, 157)',
    }

    let baseStyle2 = {
                color:active ? 'rgb(126, 190, 157)': 'black',

    }

    const myf2 =() => {
        console.log('hello');
    }

    const wrapperFunction = () => {
        handleClick();
        myf2();
    }

    return (

        <div className="rowHeader">
            <div className="timeContainer">
                <div className='title'>New Movies</div>
                <div className="toggle">
                    <button className="todayButton" onClick={wrapperFunction} style={{ backgroundColor: active ? "white" : "rgb(3, 37, 65)" , ...baseStyle}}>Today</button>
                    <button className="weekButton" onClick={handleClick} style={{ backgroundColor: active ? "rgb(	3, 37, 65)" : "white" ,...baseStyle2}}>This week</button>
                </div>

            </div>
        </div>

    );
}
