import { color } from "@mui/system";
import { useState } from "react"

export function MoviesHeader() {
    const [active, setActive] = useState(false);
    const handleClick = () => {
        setActive(!active);
    };

    let baseStyle={
        color:active ? 'active' : 'rgb(	3, 37, 65)',
    }




    return (

        <div className="rowHeader">
            <div className="timeContainer">
                <div className='title'>New Movies</div>
                <div className="toggle">
                    <button className="todayButton" onClick={handleClick} style={{ backgroundColor: active ? "white" : "rgb(	3, 37, 65)" , ...baseStyle}}>Today</button>
                    <button className="weekButton" onClick={handleClick} style={{ backgroundColor: active ? "rgb(	3, 37, 65)" : "white" }}>This week</button>
                </div>

            </div>
        </div>

    );
}
