import { useState, useEffect } from 'react';

const SearchBar = () => {
    const [searchInput, setSearch] = useState("");
    const [d, setD] = useState('');



    const handleSubmit = (event) => {
        event.preventDefault();
        // console.log(d);
    }

    const getData = () =>{
    var requestOptions = {
        method: 'GET',
        redirect: 'follow'
      };

      fetch("https://api.themoviedb.org/3/search/movie?query=&api_key=a971131533ecd1f4d0cb562ab92a94ef", requestOptions)
        .then(response => response.json())
        .then(searchInput=> setSearch(searchInput.results))
        .catch(error => console.log('error', error));
    }

    useEffect(() => {
        getData();
        console.log(searchInput);
        // our fetch codes
    }, []);
        console.log(searchInput);

    return (
        <>
            <div className="searchBar">
                <form onSubmit={handleSubmit} >
                    <input className="searchBarInput" type='search' value={d} onChange={(e) => setD(e.target.value)} placeholder="Search for a movie, tv show, person......">
                    </input>
                </form>
            </div>
           <div> {searchInput.map((item)=>
              <div className='moviePostContainer'>
              <img className='moviePoster'  src={`http://image.tmdb.org/t/p/w500/${item.poster_path}`} alt={item.title}></img>
              <div className='movieDescieption'>
                <h3>{item.original_title}</h3>
                <p>{item.release_date}</p>
                <p>{item.id}</p>
              </div>
          </div>
            )}
            </div>


        </>

    )
}

export default SearchBar;
