import { useEffect, useState } from "react";
// import { PostCard } from "./PostCard";

export function GetMovies (){
    const [data,setData] = useState([]);

    const apiGet =  () => {

    fetch('https://api.themoviedb.org/3/movie/550?api_key=a971131533ecd1f4d0cb562ab92a94ef')
    .then((res) => res.json())
    .then((data) => 
    {
        console.log(data);
        setData(data);

    });

    };

    useEffect(() => {
        apiGet();
    },[]);


    return (
        <>
        {data.map((item,key) => {
            return (
                <div className="mainDiv" key={key}>
                <div className="postContainer">
                  <img className="posterImage" src="https://media.harrypotterfanzone.com/philosophers-stone-20-years-of-movie-magic-ron-poster.jpg" alt="movie poster"></img>
                  <div className="movieDescreption">
                  <h4 className="movieTitle">{data.id}</h4>
                  <p className="pubDate">Feb.2.2012</p>
                </div>
            </div>
            </div>
            )
        }
        )}
        </>

