import { useEffect, useState } from "react";
import { CircularProgressbar,buildStyles } from "react-circular-progressbar";


export function GetMovies() {
    const [data, setData] = useState([]);
    useEffect(() => {
        function getMovies() {
            var requestOptions = {
                method: 'GET',
                redirect: 'follow'
            };

            fetch("https://api.themoviedb.org/3/movie/popular?api_key=a971131533ecd1f4d0cb562ab92a94ef", requestOptions)
                .then(response => response.json())
                .then(data => setData(data.results))
                .catch(error => console.log('error', error));
        }
        getMovies();
    }, [])
    console.log(data)
    return (
        <>
        <div className="mainDiv">
                {data.map(item =>
                    <div className="postContainer">
                        {/* <img className="posterImage" src="https://media.harrypotterfanzone.com//sANUefL2v8VI6fSfK3gWAG3XBt4.jpg" alt="movie poster"></img> */}
                        <img className="posterImage" src={`http://image.tmdb.org/t/p/w500/${item.poster_path}`} alt={item.title} />
                        <div className="movieDescreption">
                            <h4 className="movieTitle">{item.title}</h4>
                            <p className="pubDate">{item.release_date}</p>
                            <CircularProgressbar
                            value={item.vote_average}
                            text={item.vote_average}
                            styles={buildStyles({
                                // Rotation of path and trail, in number of turns (0-1)
                                rotation: 0.25,

                                // Whether to use rounded or flat corners on the ends - can use 'butt' or 'round'
                                strokeLinecap: 'butt',

                                // Text size
                                textSize: '16px',

                                // How long animation takes to go from one percentage to another, in seconds
                                pathTransitionDuration: 0.5,

                                // Can specify path transition in more detail, or remove it entirely
                                // pathTransition: 'none',

                                // Colors
                                pathColor: `rgba(62, 152, 199, ${item.vote_average / 100})`,
                                textColor: '#f88',
                                trailColor: '#d6d6d6',
                                backgroundColor: '#3e98c7',
                              })}

                            />
                        </div>
                    </div>

                )}
            </div>
        </>
    );
}
